mapper.select:
select article_id from article_long
and is_delete = #{isDelete}
and is_delete = false
and user_id = #{userId}

select al.user_id as userId, al.article_title as articleTitle, al.path as path, al.create_time as createTime, al.edit_time as editTime, al.edit_of as editOf, al.edit_count as editCount, al.is_delete as isDelete, al.is_pass as isPass, al.is_edited as isEdited, al.is_reject as isReject, al.channel_id as channelId, udt.nick_name as nickName
from article_long as al
left join article_long_summary as als on al.article_id = als.article_id
left join users_detail as udt on al.user_id = udt.user_id
al.article_id = #{articleId}
and al.is_pass = #{isPass}
and al.is_Delete = #{isDelete}
and al.is_edited = #{isEdited}
and al.is_reject = #{isReject}

mapper.update:
update
  article_long as al
set 
  al.article_id = al.article_id

    , al.is_pass = #{isPass}


    , al.is_Delete = #{isDelete}


    , al.is_reject = #{isReject}

where
  al.article_id = #{articleId}

mapper.select:
select exists(
  select 1 from article_long where user_id = #{userId} and article_id = #{articleId}
)
