mapper.insert:
insert into 
  user_attempts(
  user_id, 
  user_name
  ) 
values( 
  (select user_id from users where user_name = #{userName}),
  #{userName}
  )

mapper.update:
update 
  user_attempts 
set 
  statu = 0 


    and user_name = #{userName}


    and user_id = #{userId}

mapper.select:
select ua.*
from user_attempts as ua
right join users on ua.user_id = users.user_id
ua.statu = 1
and ua.user_name = #{userName}
and ua.user_id = #{userId}

select count(*) from user_attempts as ua
right join users on ua.user_id = users.user_id
where ua.statu = 1 and users.user_name = #{userName}

select exists( 
  select 
    1
  from 
    users
  where
    user_id = #{userId}
    and pwd = #{pwd}
)
